/**
 * Look up cleaning info for a toy by kit ID and toy name (Chinese).
 * Falls back to fuzzy matching if exact match not found.
 */
export function getCleaningInfo(kitId: string, toyNameZh: string): CleaningInfo | null {
  const key = `${kitId}::${toyNameZh}`;
  if (cleaningData[key]) return cleaningData[key];

  // Fuzzy match: try to find a key that contains the toy name
  for (const k of Object.keys(cleaningData)) {
    if (!k.startsWith(kitId + "::")) continue;
    const name = k.split("::")[1];
    if (name.includes(toyNameZh) || toyNameZh.includes(name)) {
      return cleaningData[k];
    }
  }
  return null;
}
