/**
 * Get pros/cons review for a toy.
 * Tries multiple matching strategies: exact Chinese name, fuzzy match.
 */
export function getToyReview(kitId: string, toyName: string): ToyReview | null {
  // Direct match by Chinese name
  const directKey = `${kitId}::${toyName}`;
  if (reviewData[directKey]) return reviewData[directKey];

  // Fuzzy match: try to find a key that contains the toy name or vice versa
  const prefix = `${kitId}::`;
  for (const key of Object.keys(reviewData)) {
    if (!key.startsWith(prefix)) continue;
    const reviewToyName = key.slice(prefix.length);
    if (reviewToyName.includes(toyName) || toyName.includes(reviewToyName)) {
      return reviewData[key];
    }
  }

  return null;
}
